INSERT INTO `destination` VALUES (1,'Hà Nội','Miền Bắc ','Hà Nội là thủ đô của nước Việt Nam, đồng thời cũng là một trung tâm chính trị, kinh tế, văn hóa, thương mại và du lịch quan trọng của Việt Nam.Hà Nội nằm hai bên bờ sông Hồng, ở giữa vùng Đồng bằng Bắc Bộ, tiếp giáp với các tỉnh: Thái Nguyên, Vĩnh Phúc ở phía bắc; phía nam giáp Hà Nam và Hoà Bình; phía đông giáp các tỉnh Bắc Giang, Bắc Ninh và Hưng Yên; phía tây giáp tỉnh Hoà Bình và Phú Thọ.','1',1),(2,'Huế','Miền Trung','Thừa Thiên – Huế ở vào vị trí trung độ của cả nước, nằm giữa Thủ đô Hà Nội và thành phố Hồ Chí Minh, phía Bắc giáp tỉnh Quảng Trị, phía Nam giáp thành phố Đà Nẵng, phía Tây giáp nước Cộng hoà Dân chủ Nhân dân Lào, phía Đông giáp biển Đông. Về tổ chức hành chính, Thừa Thiên – Huế có 8 huyện và thành phố Huế với 150 xã, phường, thị trấn','2',1),(3,'Sài Gòn','Miền Nam','Thành phố Hồ Chí Minh (Sài Gòn) là trung tâm kinh tế, chính trị và văn hóa lớn nhất ở miền Nam Việt Nam. Sài Gòn – TP.HCM cũng là một trung tâm du lịch lớn, với các di tích lịch sử và bảo tàng ghi lại dấu ấn thời chiến tranh chống Pháp và Mỹ. Tiêu biểu là hệ thống 11 bảo tàng trong đó nổi tiếng nhất là Bảo tàng chứng tích chiến tranh. Bên cạnh đó, các công trình kiến trúc thời thuộc địa cũng làm mãn nhãn du khách khi du lịch Sài Gòn, như Trụ sở Ủy Ban Nhân Dân Thành phố, Nhà hát lớn, Bưu điện trung tâm, Bến Nhà Rồng, Chợ Bến Thành và Dinh Độc Lập','3',1),(4,'Cần Thơ','Miền Nam','Thành phố Cần Thơ nằm trong vùng trung – hạ lưu và ở vị trí trung tâm châu thổ đồng bằng sông Cửu Long, trải dài trên 55km dọc bờ Tây sông Hậu, tổng diện tích tự nhiên 1.401,61 km2 , chiếm 3,49% diện tích toàn vùng. Phía Bắc giáp tỉnh An Giang; phía Đông giáp tỉnh Đồng Tháp và tỉnh Vĩnh Long; phía Tây giáp tỉnh Kiên Giang; phía Nam giáp tỉnh Hậu Giang','4',1),(5,' Ninh Bình','Miền Bắc ',' Ninh Bình là một tỉnh nằm ở cửa ngõ cực nam miền Bắc và khu vực đồng bằng Bắc Bộ, Việt Nam. Ninh Bình giáp với Hòa Bình, Hà Nam ở phía bắc, Nam Định ở phía đông qua sông Đáy, Thanh Hóa ở phía tây, biển (vịnh Bắc Bộ) ở phía đông nam. Trung tâm tỉnh là thành phố Ninh Bình cách thủ đô Hà Nội 93 km về phía nam','5',1),(6,'Đà Lạt','Miền Nam','Đà Lạt là một trong những thành phố du lịch nổi tiếng nhất của Việt Nam. Trong thời Pháp thuộc, tên tiếng Latin Dat Aliis Laetitiam Aliis Temperiem có nghĩa là \"cho những người này niềm vui, cho những người khác sự mát mẻ\". Đà Lạt được mệnh danh là: thành phố hoa, thành phố tình yêu, thành phố mùa xuân, thành phố sương mù','6',1),(7,'Sapa','Miền Bắc',' Nằm ở phía tây bắc của Tổ quốc, Sa Pa là một huyện vùng cao của tỉnh Lào Cai, một vùng đất khiêm nhường, lặng lẽ nhưng ẩn chứa bao điều kỳ diệu của cảnh sắc thiên nhiên. Phong cảnh thiên nhiên của Sa Pa được kết hợp với sức sáng tạo của con người cùng với địa hình của núi đồi, màu xanh của rừng, như bức tranh có sự sắp xếp theo một bố cục hài hoà tạo nên một vùng có nhiều cảnh sắc thơ mộng hấp dẫn.','7',1);
